"use strict";var storage={backpack:1,tank:1,filters:8,oxygen:.5,idle:6/7,sprint:1.2};function truncateValue(e){return parseFloat(e).toFixed(2)}function parseValue(e,t){return Math.ceil(e/t)}function parseValueTruncate(e,t){return truncateValue(e/t)}function parseValueSeconds(e,t){var a=e/t,r=Math.floor(a),n=Math.floor(60*(a-r));return r>0&&0!==n?r.toString()+" minutes "+n.toString()+" seconds":0===r?n.toString()+" seconds":r.toString()+" minutes"}function updateFields(e){for(var t=document.getElementsByClassName("output"),a=0;a<t.length;a++){var r=t[a];"total"===r.id?r.innerHTML=truncateValue(e):r.className.indexOf("time")>-1?r.innerHTML=parseValueSeconds(e,storage[r.id]):r.className.indexOf("truncate")>-1?r.innerHTML=parseValueTruncate(e,storage[r.id]):r.innerHTML=parseValue(e,storage[r.id])}}function calc(){var e=0;for(var t in storage)if(storage.hasOwnProperty(t)){var a=document.getElementById(t);"any"===a.step?e+=storage[t]*(parseFloat(a.value)||0):e+=storage[t]*(parseInt(a.value)||0)}updateFields(e)}