"use strict";var storage={backpack:1,tank:1,filters:8,oxygen:.5,units:.1,idle:6/7,sprint:1.2};function truncateValue(e){return parseFloat(e).toFixed(2)}function parseValue(e,t){return Math.ceil(e/t)}function parseValueTruncate(e,t){return truncateValue(e/t)}function parseValueSeconds(e,t){var a=e/t,n=Math.floor(a),r=Math.floor(60*(a-n));return n>0&&0!==r?n.toString()+" minutes "+r.toString()+" seconds":0===n?r.toString()+" seconds":n.toString()+" minutes"}function updateFields(e){for(var t=document.getElementsByClassName("output"),a=0;a<t.length;a++){var n=t[a];"total"===n.id?n.innerHTML=truncateValue(e):n.className.indexOf("time")>-1?n.innerHTML=parseValueSeconds(e,storage[n.id]):n.className.indexOf("truncate")>-1?n.innerHTML=parseValueTruncate(e,storage[n.id]):n.innerHTML=parseValue(e,storage[n.id])}}function calc(){var e=0;for(var t in storage)if(storage.hasOwnProperty(t)){var a=document.getElementById(t);"any"===a.step?e+=storage[t]*(parseFloat(a.value)||0):e+=storage[t]*(parseInt(a.value)||0)}updateFields(e)}