var storage={smallbatt:32,medbatt:256,cells:48,medrover:8,largerover:16,tractor:4,buggy:4,backpack:10},resources={organic:100,carbon:300};function truncateValue(e){return parseFloat(e).toFixed(2)}function parseValue(e,a){return Math.ceil(e/a)}function updateFields(e){for(var a=document.getElementsByClassName("output"),r=0;r<a.length;r++){var t=a[r];"total"===t.id?t.innerHTML=truncateValue(e):t.innerHTML=parseValue(e,resources[t.id])}}function calc(){var e=0;for(var a in storage)storage.hasOwnProperty(a)&&(e+=storage[a]*(parseInt(document.getElementById(a).value)||0));updateFields(e)}