var storage={smallbatt:32,medbatt:256,cells:48,medrover:8,largerover:16,tractor:4,buggy:4,backpack:10,power:32,organic:100,carbon:300};function truncateValue(e){return parseFloat(e).toFixed(2)}function parseValue(e,a){return truncateValue(e/a)}function updateFields(e){for(var a=document.getElementsByClassName("output"),t=0;t<a.length;t++){var r=a[t];"total"===r.id?r.innerHTML=truncateValue(e):r.innerHTML=parseValue(e,storage[r.id])}}function calc(){var e=0;for(var a in storage)storage.hasOwnProperty(a)&&(e+=storage[a]*(parseInt(document.getElementById(a).value)||0));updateFields(e)}